    private void btnCalcActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcActionPerformed
        // TODO add your handling code here:
        try{
        double radius = Double.parseDouble(txtRadius.getText());
         CircleClass circleclass = new CircleClass(radius);
         StringBuilder results= new StringBuilder(txtResult.getText());
         results.append("\nRadius:");
        results.append(radius);
        results.append("\n result circle area");
        results.append (circleclass.getAreaCircle());
        results.append("\n result circumference");
        results.append(circleclass.GetCircumference());
        txtResult.setText(results.toString());
        }
        catch(Exception e){
            JOptionPane.showMessageDialog(this, "An error");
        }
    }//GEN-LAST:event_btnCalcActionPerformed

